from Simulations import *
from Distributions import *
from Quality_metrics import *
from Loading_param import *
import matplotlib.pyplot as plt
import numpy as np


# Normal Distribution
"""Change Mean"""
#Ranking_length:100 ; Number_of_rankings:200; mean: range(0,200)
d = Load("Parameters.txt")
print(d)
mean_upper_limit = d["mean_upper_limit"]
mean_lower_limit = d["mean_lower_limit"]


for std in [125,150,200]:
    print("std = ", std)
    Ranking_change_mean_random = list()
    Ranking_change_mean_Asc = list()
    Ranking_change_mean_Desc = list()
    for mean in range(int(d["mean_lower_limit"]), int(d["mean_upper_limit"])):
        Ranking_change_mean_random.append(rankings(d, Order_by="Random", mean1=mean, std1=std))
        Ranking_change_mean_Asc.append(rankings(d, Order_by="Ascending", mean1=mean, std1=std))
        Ranking_change_mean_Desc.append(rankings(d, Order_by="Descending", mean1=mean, std1=std))

    for pos in [0.1, 0.3, 0.5, 0.8]:
        d["pos"] = pos
        print("pos= ", pos)
        print("RNAKING CHANGE MEAN RANDOM", Ranking_change_mean_random)
        recall_mean_random = Measuring_quality (Ranking_change_mean_random,"recall_at_k", d)
        print(len(recall_mean_random))
        recall_mean_Asc  = Measuring_quality (Ranking_change_mean_Asc,"recall_at_k", d)
        recall_mean_Desc = Measuring_quality (Ranking_change_mean_Desc,"recall_at_k", d)


        plt.plot(range(int(d["mean_lower_limit"]),int(d["mean_upper_limit"])), recall_mean_random, color="red", label="Random Rank Allocation")
        plt.plot(range(int(d["mean_lower_limit"]),int(d["mean_upper_limit"])), recall_mean_Asc, color="blue", label="Ascending Rank Allocation")
        plt.plot(range(int(d["mean_lower_limit"]),int(d["mean_upper_limit"])), recall_mean_Desc, color="green", label="Descending Rank Allocation")
        plt.xlabel("Mean")
        plt.ylabel("Recall at K")
        plt.legend()
        plt.title("Recall at K: Vary mean: std= " + str(std) + " pos= "  +str(d["pos"]) + " R length= " +str(d["Ranking_length"]))
        #plt.savefig("C:/Users/ziedk/PycharmProjects/Master Thesis/Plots/Normal Distribution/Mean/Recall/std= "+ str(std) + " pos= "  +str(d["pos"]) + " R length= " +str(d["Ranking_length"])+".png")
        plt.show()



